import { useTheme } from './ThemeContext';
const ThemedButton = () =>{
    const{theme,toggleTheme}=useTheme();
    return(
        <button
        style={{backgroundColor: theme ==='light' ? 'gr' : '#333',
        color: theme=== 'light' ? '#333' :  '#fff', width:200, height:100}}
        onClick={toggleTheme}>
            Toggle Theme
        </button>
    );
};
export default ThemedButton;
