import React from 'react';
import { ThemeProvider } from './ThemeContext';
import ThemedButton from './ThemedButton';
import ThemedMessage from './ThemedMessage';
import { useTheme } from './';

const App = () => {
  const { backgroundColor } = useTheme();

  return (
    <div style={{ background: backgroundColor, minHeight: '100vh', transition: 'background-color 0.5s', textAlign: 'center', marginTop: '50px' }}>
      <ThemeProvider>
        {/* Votre contenu avec le th√®me et la couleur de fond */}
        <ThemedButton />
        <ThemedMessage />
      </ThemeProvider>
    </div>
  );
};

export default App;













