import { useTheme } from './ThemeContext';


const ThemedButton = () =>{
    const{theme,toggleTheme,backgroundColor}=useTheme();
    return(

    <div  style={{
        textAlign: 'center',
          marginTop: '50px',
          transition: 'background-color 0.5s',
          backgroundColor,
          minHeight: '1000vh',  }}>
        <button
        style={{backgroundColor: theme ==='light' ? 'red' : 'yellow',
        color: theme=== 'light' ? 'white' :  'black', width:200, height:100}}
        onClick={toggleTheme}>
            Toggle Theme
        </button>
        <p style={{color: theme=== 'light' ? '#333' : 'blue'}}>
        this is a themed message.
        </p>
        </div>
    );
};
export default ThemedButton;
