import { useTheme } from './ThemeContext';


// Dans le composant ThemedButton.js par exemple
const ThemedButton = () => {
    const { theme, toggleTheme, backgroundColor } = useTheme();
    console.log({ theme, backgroundColor });

    return (
      <button
        style={{
          backgroundColor: theme === 'light' ? 'red' : 'yellow',
          color: theme === 'light' ? 'white' : 'black',
          width: 200,
          height: 100,
          backgroundColor: backgroundColor,
        }}
        onClick={toggleTheme}
      >
        Toggle Theme
      </button>
    );
  };
export default The  








