import { ThemeProvider } from './ThemeContext';
import ThemedButton from './ThemedButton';
import ThemedMessage from './ThemedMessage';
const App = () => {
    const { theme, toggleTheme, backgroundColor } = useTheme();
    console.log({ theme, backgroundColor });

    return (
      <ThemeProvider>
        <div style={{ textAlign: 'center', marginTop: '50px', transition: 'background-color 0.5s', backgroundColor }}>
          <ThemedButton />
          <ThemedMessage />
        </div>
      </ThemeProvider>
    );
  };

export default App













