import { useTheme } from './ThemeContext';
const ThemedMessage=()=>{
    const {theme,backgroundColor}=useTheme();
    return (

      <div  style={{
        textAlign: 'center',
          marginTop: '50px',
          transition: 'background-color 0.5s',
          backgroundColor,
          minHeight: '100vh',  }}>
        <p style={{color: theme=== 'light' ? '#333' : 'blue'}}>
        this is a themed message.
        </p>
        </div>
    );
};
export default ThemedMessage;
