// Importez ThemeProvider et useTheme de votre fichier ThemeContext
import { ThemeProvider, useTheme } from './ThemeContext';

const App = () => {
  // Utilisez useTheme pour accéder aux propriétés du thèmeconst { backgroundColor } = useTheme();

return (
    // Enveloppez votre application avec le ThemeProvider
    <ThemeProvider>
      {/* Utilisez la couleur de fond du thème dans le style du div */}
      <div style={{ textAlign: 'center', marginTop: '50px', transition: 'background-color 0.5s', backgroundColor }}>
        {/* Incluez vos composants qui utilisent le thème */}
        <ThemedButton />
        <ThemedMessage />
      </div>
    </ThemeProvider>
  );
};

export default App;














