import React from 'react';
import { ThemeProvider, useTheme } from './ThemeContext';
import ThemedButton from './ThemedButton';
import ThemedMessage from './ThemedMessage';

const App = () => {
  const { backgroundColor,toggleTheme } = useTheme();

  return (
    <ThemeProvider>

      <div  style={{
        textAlign: 'center',
          marginTop: '50px',
          transition: 'background-color 0.5s',
          backgroundColor,
          minHeight: '100vh',  }} onClick={toggleTheme}>
        <ThemedButton />
        <ThemedMessage />
      </div>
    </ThemeProvider>
  );
};

export default App;














