// Importez ThemeProvider et useTheme de votre fichier ThemeContext

import  { useTheme,ThemeProvider } from './ThemeContext';
import ThemedButton from './ThemedButton';
import ThemedMessage from './ThemedMessage';
import {backgroundColor} from './ThemeContext';

const App = () => {
  // Utilisez useTheme pour accéder aux propriétés du thème
  const { theme, toggleTheme, backgroundColor } = useTheme();

return (
    // Enveloppez votre application avec le ThemeProvider
    <ThemeProvider>
      {/* Utilisez la couleur de fond du thème dans le style du div */}
      <div style={{
        backgroundColor: theme === 'light' ? 'red' : 'yellow',
        color: theme === 'light' ? 'white' : 'black',
        width: 200,
        height: 100,
        backgroundColor: backgroundColor, transition: 'background-color 0.5s' }}>
        {/* Incluez vos composants qui utilisent le thème */}
        <ThemedButton />
        <ThemedMessage />
      </div>
    </ThemeProvider>
  );
};

export default App;














